import React from 'react'
import {Link} from 'react-router-dom'
import {connect} from 'react-redux'

import {
    getTotalBasketCount,
    getTotalBasketPrice
} from 'selectors'

const BasketCart = ({totalBasketCount, totalPrice}) => {
    return (
        <div className="card">
            <div className="dropdown">
                <Link
                to='/basket'
                id='dLabel'
                className="btn btn-inverse btn-blick btn-lg"
                >
                    <i className="fa fa-fa-shopping-cart"></i>
                    <span>{totalBasketCount} смартфонов - {totalPrice}p</span>
                </Link>
            </div>
        </div>
    )
}

const mapStateToProps = state => {
    return {
        totalBasketCount: getTotalBasketCount(state),
        totalPrice: getTotalBasketPrice(state)
    }
}

export default connect(mapStateToProps, null)(BasketCart)